mapper.select:
SELECT postNum, postTitle, postWriter, postContent, postTime, viewCount
	FROM board_table
	WHERE postNum=#{postNum}

mapper.update:
UPDATE board_table
	SET viewCount = viewCount + 1
	WHERE postNum=#{postNum}

UPDATE board_table
	SET postTitle=#{postTitle}, postWriter=#{postWriter}, postContent=#{postContent}
	WHERE postNum=#{postNum}
